	end case;
	sizeImgH1<=currentIndexFin1-currentIndexDebut1;
    if(posH=640 and posV=480) then
        indexImgH1<=sizeImgH1-1;
        indexImgV1<=sizeImgV-1;
        indexImgH2<=sizeImgH1-1;
        indexImgV2<=sizeImgV-1;
        indexImgH3<=sizeImgH1-1;
        indexImgV3<=sizeImgV-1;
    end if;
    if(videoOn='1') then
            if(posH>posImgH1 and posH<=posImgH1+sizeImgH1 and posV>posImgV1 and posV<=posImgV1+sizeImgV)then
                imgTmp<=img(indexImgV1,sizeImgHTotal-currentIndexFin1+indexImgH1);
                red_out<=imgTmp(11 downto 8);
                green_out<=imgTmp(7 downto 4);
                blue_out<=imgTmp(3 downto 0);
				
                if(indexImgH1=0) then
                    indexImgH1<=sizeImgH1-1;
                    indexImgV1<=indexImgV1-1;
				else
					indexImgH1<=indexImgH1-1;
                end if;
                if(indexImgV1=0) then
                    indexImgV1<=sizeImgV-1;
                end if;
            elsif(posH>posImgH2 and posH<=posImgH2+sizeImgH2 and posV>posImgV1 and posV<=posImgV1+sizeImgV)then
                imgTmp<=img(indexImgV2,sizeImgHTotal-currentIndexFin2+indexImgH2);
                red_out<=imgTmp(11 downto 8);
                green_out<=imgTmp(7 downto 4);
                blue_out<=imgTmp(3 downto 0);
				
                if(indexImgH2=0) then
                    indexImgH2<=sizeImgH2-1;
                    indexImgV2<=indexImgV2-1;
				else
					indexImgH2<=indexImgH2-1;
                end if;
                if(indexImgV2=0) then
                    indexImgV2<=sizeImgV-1;
                end if;
            elsif(posH>posImgH3 and posH<=posImgH3+sizeImgH3 and posV>posImgV1 and posV<=posImgV1+sizeImgV)then
                imgTmp<=img(indexImgV3,sizeImgHTotal-currentIndexFin3+indexImgH3);
                red_out<=imgTmp(11 downto 8);
                green_out<=imgTmp(7 downto 4);
                blue_out<=imgTmp(3 downto 0);
				
                if(indexImgH3=0) then
                    indexImgH3<=sizeImgH3-1;
                    indexImgV3<=indexImgV3-1;
				else
					indexImgH3<=indexImgH3-1;
                end if;
                if(indexImgV3=0) then
                    indexImgV3<=sizeImgV-1;
                end if;
            else
                red_out<=red_in;
                green_out<=green_in;
                blue_out<=blue_in;
            end if;            
    else
        red_out<=(others =>'0');
        green_out<=(others =>'0');
        blue_out<=(others =>'0');
    end if; 
    score<=scoreCentaine*100+scoreDizaine*10+scoreUnit;        
end if;

end process;

end Behavioral;
